sources.styles/blocks/menu.scss:
.menu {
  position: fixed;
  transform: translateX(200%);
  background-color: $c-gray;
  height: 100vh;
  top: 0;
  left: 0;
  right: 0;
  overflow: none;
  padding-top: 43px;
  transition: $transition;
  @include onDesktop {
    background-color: $c-gray;
    position: relative;
    transform: translateX(0);
    display: flex;
    justify-content: center;
    padding-top: 0;
    margin-top: 200px;
    height: auto;
   }

  &__top {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  &__logo {
    cursor: pointer;
    transition: $transition;
    &:hover {
      transform: scale(1.1);
    }
    @include onDesktop {
      display: none;
    }
  }

  &__nav {
    display: flex;
    flex-direction: column;
    gap: 25px;
    margin-top: 30px;
    @include onTablet {
      margin-top: 64px;
      gap: 40px;
    }
    @include onDesktop {
      flex-direction: row;
      justify-content: space-between;
      width: 578px;
      margin-top: 0;
    }
  }

  &__list {
    list-style: none;
  }

  &__item {
    font-size: 14px;
    font-weight: 400;
    line-height: 20px;
    text-decoration: none;
    color: $black;
    transition: $transition;
    cursor: pointer;
    &:hover {
      box-shadow: 10px 5px 5px #a99f9f;;
    }
    span {
      font-weight: 700;
    }
    @include onTablet {
      font-size: 22px;
      font-weight: 400;
      line-height: 28px;
      span {
        font-weight: 700;
      }
    }
    @include onDesktop {
      font-size: 14px;
      line-height: 20px;
    }
  }

  &__close {
    width: 16px;
    height: 16px;
    @include onDesktop {
      display: none;
     }
  }

  &__info {
    display: flex;
    margin-top: 50px;
    justify-content: left;
    gap: 20px;
  }

  &__specs {
    @include styleSpecs;
    @include onTablet {
      display: none;
    }
  }

  &__language {
    @include styleLanguage;
    @include onTablet {
      display: none;
    }
  }

  &__buy {
    width: 100%;
    height: 48px;
    margin-top: 162px;
    @include styleButton;
    @include onTablet {
      display: none;
    }
  }
}

.activeLang {
  color: $black;
  font-weight: 400;
}

