        display: block;
      }
      &--2,
      &--4 {
        display: block;
        grid-column: 5 / -1;
      }
    }

    @include onDesktop {
      width: 262px;
      &--1 {
        grid-column: 1 / 4;
      }
      &--2 {
        grid-column: 4 / 7;
      }
      &--3 {
        grid-column: 7 / 10;
      }
      &--4 {
        grid-column: 10 / -1;
      }
    }
  }

    &__img {
      display: block;
      margin: 0 auto;
      width: 40px;
      height: 50px;
      transition: $transition;
      cursor: pointer;
      &:hover {
