    &__title {
      text-align: center;
      font-size: 22px;
      font-weight: 700;
      line-height: 28px;
      color: $dark-grey;
      margin-top: 40px;
    }

    &__subtitle {
      text-align: center;
      font-size: 14px;
      font-weight: 400;
      line-height: 21px;
      color: $gray;
      margin-top: 20px;
      @include onTablet {
        margin-top: 21px;
      }
      @include onDesktop {
      &--1,
      &--3 {
        margin-top: 52px;
      }
      &--2,
      &--4 {
        margin-top: 24px;
      }
      }
    }
}

