    @include pageGrid;
    height: 600px;
    padding: 0 30px;
    margin-top: 32px;
    background-color: #d6ecec;
    position: absolute;
    left: 0;
    right: 0;
    @include onTablet {
      position: static;
      left: auto;
      right: auto;
      padding: 0;
    }
    @include onDesktop {
     height: 682px;
    }
  }

  &__dignity:nth-child(3) {
    grid-column: 1/ -1;
    @include onDesktop {
      grid-column: 2 / 6;
    }
  }

  &__dignity:nth-child(1),
  &__dignity:nth-child(2) {
      display: none;
      @include onDesktop {
        display: block;
      }
  }

  &__dignity:nth-child(2) {
    @include onDesktop {
      grid-column: 7 / 10;
    }
  }

  &__dignity:nth-child(1) {
    @include onDesktop {
      grid-column: 2 / 6;
    }
  }

