sources.styles/_resets.scss:
html,
body {
  margin: 0;
  scroll-behavior: smooth;
}

body {
  background: #f7f7f7;
}

div,
p,
span,
h1,
h2,
h3,
ul,
li,
a,
section,
img,
input,
button,
nav,
video {
  margin: 0;
  padding: 0;
}

* {
  box-sizing: border-box;
}

a {
  text-decoration: none;
  color: inherit;
}

button,
video {
  border: none;
  outline: none;
  cursor: pointer;
}

ul {
  list-style: none;
}

sources.styles/container.scss:
.container {
  &__header {
    padding-top: 13px;
  }

  &__header,
  &__promo,
  &__nav,
  &__benefits {
    width: 100%;
    padding-left: 10vw;
    padding-right: 10vw;
    max-width: 1440px;
    margin: auto;
    position: relative;
  }

  &__features-section {
    padding-top: 150px;
  }
}

@media (max-width: $breakPointTablet) {
  .container {
    &__header {
      padding-top: 28px;
    }

    &__features-section {
      padding-top: 130px;
    }
  }
}

@media (max-width: $breakPointLaptop) {
  .container {
    &__header,
    &__promo,
    &__nav,
    &__benefits,
    &__testimonials {
      padding-right: 40px;
      padding-left: 40px;
    }
  }
}

@media (max-width: $breakPointMobileSmall) {
  .container {
    &__header,
    &__promo,
    &__benefits,
    &__testimonials {
      padding-right: 20px;
      padding-left: 20px;
    }
  }

  .promo-image {
    padding-left: 0;
    padding-right: 0;
  }
}

sources.styles/header.scss:
.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 89px;
  text-transform: uppercase;

  &__logo {
    width: 161px;
    height: 26px;
    transition: 0.3s;
    position: relative;
    z-index: 2;

    &:hover {
      transform: scale3d(1.2, 1.2, 1.2) translateX(10px);
    }
  }

  &__specs {
    @include fontSet("Helvetica Neue Light", 14px, 17px);
    font-weight: 300;
    color: $colorSpecs;
    transition: 0.3s;

    &:hover {
      color: $colorHover;
    }

  }

  &__options {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 269px;
  }

  &__language-option {
    @include fontSet("Helvetica Neue Roman", 14px, 18px);
    color: $colorText;
    font-weight: 400;
    cursor: pointer;
  }

  &__toggler-language {
    display: none;
  }

  &__language-option::before {
    content: "EN ";
    font-weight: 400;
  }

  &__toggler-language:checked + &__language-option::before {
    @include fontSet("Helvetica Neue Roman", 14px, 18px);
    font-weight: 300;
    color: $inactiveColor;
  }

  &__language-option::after {
    content: " UA";
    font-weight: 300;
    color: $inactiveColor;
  }

  &__toggler-language:checked + &__language-option::after {
    font-weight: 400;
    color: $colorText;
  }

  &__button {
    width: 102px;
    height: 48px;
    background-color: $colorText;
    color: $colorButtonText;
    @include fontSet("Helvetica Neue Medium", 12px, 15px);
    font-weight: 500;
    text-transform: uppercase;
    transition: 0.3s;

    &:hover {
      color: $colorText;
      background-color: $colorHover;
    }
  }
}

@media (max-width: $breakPointMobile) {
  .header {
    margin-bottom: 22px;

    &__options {
      display: none;
    }
  }
}

sources.styles/_mixins.scss:
@mixin fontSet($famaly, $size, $line) {
  font-family: #{$famaly};
  font-size: #{$size};
  line-height: #{$line};
  font-style: normal;
}

@mixin positionButton($top, $left) {
  top: #{$top};
  left: #{$left};
}

sources.styles/menu.scss:
.menu {
  display: none;

  &__toggler {
    display: none;
  }

  &__label {
    background-image: url("../images/menu.svg");
    background-position: top;
    background-repeat: no-repeat;
    width: 24px;
    height: 16px;
    z-index: 2;
    position: fixed;
    top: 28px;
    right: 40px;
    cursor: pointer;
  }

  &-nav {
    position: absolute;
    top: -600%;
    right: 0;
    left: -100%;
    background-color: rgba(208, 208, 208, 0.4);
    z-index: 1;
    transition: all 0.5s;

    &__link {
      display: inline-block;

      &:hover {
        color: $colorHover;
        font-weight: 600;
      }
    }

    &__list {
      margin: 100px 0 45px;
      margin-right: 11vw;
      text-align: right;
    }

    &__item {
      @include fontSet("Helvetica Neue Medium", 16px, 20px);
      font-weight: 500;
      color: $colorText;
      margin-bottom: 20px;
    }
  }
}

@media (max-width: $breakPointMobile) {
  .menu {
    display: block;

    &__toggler:checked ~ &__label {
      background-image: url("../images/cross.svg");
      position: fixed;
    }

    &__toggler:checked ~ &-nav {
      top: 0;
      position: fixed;
    }
  }
}

@media (max-width: $breakPointMobileSmall) {
  .menu__label {
    right: 20px;
  }
}

sources.styles/promo.scss:
