sources.styles/nav.scss:
.nav {
  margin: 50px auto 150px;
  background-color: $backgroundColor;
  position: sticky;
  top: 0;
  z-index: 10;

  &__list {
    position: sticky;
    display: grid;
    justify-content: center;
    align-items: center;
    margin: 0 32%;
    grid-column-gap: 45px;
    grid-template-columns: repeat(5, 1fr);
  }

  &__link {
    display: flex;
    justify-content: center;
    align-items: center;
    @include fontSet("Helvetica Neue Light", 14px, 21px);
    font-weight: 300px;
    text-transform: uppercase;
    color: $colorText;
    height: 76px;
    width: 120px;
    transition: 0.3s;

    &:hover {
      @include fontSet("Helvetica Neue Medium", 14px, 21px);
      font-weight: 500;
      color: $activColorText;
    }
  }
}

@media (max-width: $breakPointTablet) {
  .nav {
    margin: 100px 0;

    &__list {
      margin: 0 5%;
      grid-column-gap: min(5px);
    }
  }
}

@media (max-width: $breakPointMobile) {
  .nav {
    display: none;
  }
}

sources.styles/benefits.scss:
.benefits {
  display: grid;
  justify-content: center;
  grid-column-gap: 30px;
  grid-template-columns: repeat(4, minmax(210px, 1fr));

  &__item {
    text-align: center;
  }

  &__image {
    width: 50px;
    height: 44px;
    margin-bottom: 40px;
    transition: 0.3s;
    cursor: pointer;

    &:hover {
      transform: rotate(360deg);
    }
  }

  &__title {
    @include fontSet("DIN Pro Medium", 24px, 30px);
    text-transform: uppercase;
    color: $colorText;
    height: 60px;
    margin-bottom: 20px;
  }

  &__text {
    @include fontSet("Helvetica Neue Roman", 14px, 21px);
    font-weight: 400;
    color: $colorTextMain;
  }
}

@media (max-width: $breakPointTablet) {
  .benefits {
    grid-template-columns: repeat(2, 1fr);
    grid-row-gap: 50px;
  }
}

@media (max-width: $breakPointMobile) {
  .benefits {
    justify-self: center;
    grid-template-columns: repeat(1, 1fr);
    max-width: 630px;
    margin-top: 89px;

    &__image {
      width: 58px;
      height: 46px;
    }
  }
}

