sources.styles/features.scss:
.features {
  position: relative;
  display: flex;
  flex-direction: column;
  height: 931px;
  max-width: 1317px;
  margin-left: 10vw;

  &__title {
    @include fontSet("DIN Pro", 56px, 70px);
    text-transform: uppercase;
    color: $colorText;
    padding-left: 20px;

    &-list {
      @include fontSet("DIN Pro Medium", 34px, 40.8px);
      color: $colorValid;
      text-transform: uppercase;
      margin-bottom: 38px;
      cursor: pointer;
      transition: 0.3s;

      &:hover {
        color: $colorHover;
      }
    }

    &-list--top {
      margin-bottom: 44px;
    }
  }

  &-content {
    position: absolute;
    z-index: 1;
    max-width: 350px;
  }

  &__content {
    position: absolute;
    align-self: flex-end;
    background-color: $colorContent;
    width: 85%;
    height: 75%;
    bottom: 20px;
    right: 0;

    &-sound {
      top: -15%;
      left: 28.7%;
    }

    &-connectivity {
      top: 22%;
      left: -7.9%;
    }

    &-app {
      top: 75%;
      left: 10.3%;
    }
  }

  &__item {
    @include fontSet("Helvetica Neue Roman", 16px, 19px);
    font-weight: 400;
    color: $colorText;
    margin-bottom: 13px;
    position: relative;
    cursor: pointer;

    @media (max-width: $breakPointMobile) {
      margin-left: 15px;
    }

    &::before {
      cursor: pointer;
      position: absolute;
      content: url("../images/bullet.svg");
      width: 15px;
      height: 6px;
      top: -15%;
      right: 100%;
    }

    &:hover::before {
      content: url("../images/bullet_hover.svg");
    }
  }

  &__image {
    position: absolute;
    width: 70%;
    height: 72%;
    right: 10vw;
    bottom: 16px;
  }
}

@media (max-width: $breakPointLaptop) {
  .features {
    margin-left: 40px;

    &__image {
      right: 40px;
    }
  }
}

@media (max-width: $breakPointTablet) {
  .features {
    &__title-list--top {
      margin-bottom: 38px;
    }
  }
}

@media (max-width: $breakPointMobile) {
  .features {
    background-color: $colorContent;
    margin-left: 0;
    height: 100%;

    &-section {
      padding-top: 0;
    }

    &__title {
      @include fontSet("DIN Pro", 37px, 60px);
      padding-left: 40px;
      padding-top: 45px;

      &-list {
        @include fontSet("DIN Pro Medium", 30px, 37px);
        margin: 51px 0 20px;
      }
    }

    &-content {
      position: static;
      z-index: 0;
    }

    &__content {
      position: static;
      display: flex;
      flex-direction: column;
      width: 100%;
      padding-left: 40px;

      &-connectivity {
        padding-left: 35%;
        padding-right: 40px;
        max-width: none;
      }
    }

    &__item {
      @include fontSet("Helvetica Neue Roman", 14px, 21px);
      margin-bottom: 10px;

      &:last-child {
        margin-bottom: 0;
      }
    }

    &__image {
      position: static;
      min-width: 344px;
      min-height: 222px;
      align-self: flex-end;
      padding-right: 40px;
      margin-bottom: 45px;
    }
  }

  .container__features-section {
    padding-top: 0;
  }
}

@media (max-width: $breakPointMobileSmall) {
  .features {
    &__title {
      padding-left: 20px;
    }

    &__content {
      padding-left: 20px;

      &-connectivity {
        padding-left: 0;
        max-width: 350px;
      }
    }

    &__image {
      padding-right: 20px;
      margin: 45px 0;
    }
  }
}

sources.styles/questions.scss:
.questions {
  display: flex;
  justify-content: space-between;
  margin: 0 auto 140px;
  max-width: 1020px;
  padding-top: 150px;

  &__title {
    align-self: flex-start;
    @include fontSet("DIN Pro", 56px, 72px);
    text-transform: uppercase;
    color: $colorText;
    width: calc(50% - 10px);
    max-width: 480px;
  }

  &__form {
    display: flex;
    flex-direction: column;
    width: calc(50% - 10px);
    max-width: 438px;
  }

  &__input {
    padding-left: 30px;
    padding-top: 15px;
    padding-bottom: 14px;
    @include fontSet("Helvetica Neue Roman", 16px, 19px);
    font-weight: 500;
    color: $colorText;
    border: 1px solid $colorInput;

    &:focus {
      outline-color: $colorInput;
    }

    &:valid:required {
      outline-color: $colorValid;
    }

    &:not(:placeholder-shown):invalid {
      outline-color: $colorInvalid;
    }

    &::placeholder {
      @include fontSet("Helvetica Neue Roman", 16px, 19px);
      font-weight: 500;
      color: $inactiveColor;
    }
  }

  &__textarea {
    padding-top: 16px;
    height: 128px;
    margin: 19px 0 30px;
    resize: none;
  }

  &__button {
    width: 102px;
    height: 48px;
    background-color: $colorHover;
    @include fontSet("Helvetica Neue Medium", 12px, 15px);
    font-weight: 500;
    text-transform: uppercase;
    color: $colorButtonText;
    text-align: center;

    &:hover {
      background-color: $colorValid;
    }
  }
}

.container__questions-section {
  @media (max-width: $breakPointLaptop) {
    margin-right: 40px;
    margin-left: 40px;
  }

  @media (max-width: $breakPointMobileSmall) {
    margin-right: 20px;
    margin-left: 20px;
  }
}

@media (max-width: $breakPointTablet) {
  .questions {
    padding-top: 130px;
    margin-bottom: 88px;

    &__title {
      @include fontSet("DIN Pro", 56px, 70px);
      max-width: none;
    }

    &__input {
      max-width: none;
    }

    &__textarea {
      height: 201px;
    }

    &__button {
      height: 40px;
    }
  }
}

@media (max-width: $breakPointMobile) {
  .questions {
    flex-direction: column;
    padding-top: 45px;
    width: 100%;
    margin-bottom: 43px;

    &__title {
      @include fontSet("DIN Pro", 37px, 46px);
      margin-bottom: 30px;
      width: 100%;
      max-width: none;
    }

    &__form {
      width: 100%;
      max-width: none;
    }
  }
}

sources.styles/footer.scss:
.footer {
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 0 auto;
  align-items: center;
  position: relative;
  max-width: 942px;
  padding-bottom: 100px;

  &__arrow {
    position: absolute;
    background-image: url("../images/arrow.svg");
    width: 20px;
    height: 24px;
    top: 8px;
    right: 14px;

    @media (max-width: $breakPointMobile) {
      right: 0;
    }
  }

  @media (max-width: $breakPointLaptop) {
    margin-left: 10vw;
    margin-right: 10vw;
  }

  &__logo {
    width: 161px;
    height: 26px;
    transition: 0.5s;
    cursor: pointer;

    &:hover {
      transform: scale3d(1.2, 1.2, 1.2);
    }
  }

  &__socials {
    display: flex;
    align-items: center;
    margin-top: 63px;
    margin-bottom: 36px;
  }

  &__icon {
    &-facebook {
      background-image: url("../images/facebook.svg");
      width: 11px;
      height: 19px;
      transition: 0.3s;

      &:hover {
        background-image: url("../images/facebook_hover.svg");
        transform: scale(1.5);
      }
    }

    &-twitter {
      background-image: url("../images/twitter.svg");
      width: 22px;
      height: 17px;
      margin-right: 43px;
      margin-left: 43px;
      transition: 0.3s;

      &:hover {
        background-image: url("../images/twitter_hover.svg");
        transform: scale(1.5);
      }
    }

    &-instagram {
      background-image: url("../images/instagram.svg");
      width: 18px;
      height: 18px;
      transition: 0.3s;

      &:hover {
        background-image: url("../images/instagram_hover.svg");
        transform: scale(1.5);
      }
    }
  }

  &__copyright {
    @include fontSet("Helvetica Neue Light", 14px, 21px);
    color: $inactiveColor;
    font-weight: 300px;
  }
}

@media (max-width: $breakPointTablet) {
  .footer {
    max-width: none;
    margin-left: 40px;
    margin-right: 40px;
    padding: 42px 0 50px;
  }
}

@media (max-width: $breakPointMobile) {
  .footer {
    padding-top: 57px;

    &__logo {
      display: none;
    }

    &__copyright {
      @include fontSet("Helvetica Neue Light", 12px, 21px);
      text-align: center;
    }

    &__socials {
      margin-top: 0;
    }
  }
}

@media (max-width: $breakPointMobileSmall) {
  .footer {
    max-width: none;
    margin-left: 20px;
    margin-right: 20px;
  }
}
