sources.styles/utils/_mixins.scss:
@mixin hover($_property, $_toValue) {
  transition: #{$_property} 0.3s;
  &:hover {
    #{$_property}: $_toValue;
  }
}

@mixin onTablet {
  @media (min-width: 744px) {
    @content;
  }
}

@mixin onDesktop {
  @media (min-width: 1280px) {
    @content;
  }
}


@mixin pageGrid {
  --columns: 4;
  display: grid;
  column-gap: 20px;
  grid-auto-flow: row;
  grid-template-columns: repeat(var(--columns), 1fr);
  @include onTablet {
    --columns: 8;
    grid-template-columns: repeat(auto-fill, 1fr);
  }
  @include onDesktop {
    --columns: 12;
    column-gap: 30px;
  }
}

@mixin styleSpecs {
  font-family: $FFHN;
  font-size: 14px;
  font-weight: 300;
  line-height: 17px;
  color: $gray;
}

@mixin styleLanguage {
  font-family: $FFHN;
  font-size: 14px;
  line-height: 18px;
  font-weight: 300;
  color: $gray;
  cursor: pointer;
}

@mixin styleButton {
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: $FFHN;
  font-size: 12px;
  font-weight: 500;
  line-height: 15px;
  width: 100%;
  height: 48px;
  background-color: $dark-grey;
  border: none;
  color: $white;
  transition: $transition;
  &:hover {
    background-color: $black;
  }
}

@mixin footerStyleInput {
  font-size: 16px;
  font-weight: 400;
  line-height: 24px;
  text-align: left;
  color: #bdbdbd;
  border: 1px solid #828282;
  padding-left: 20px;
  width: 260px;
  outline: none;
  transition: $transition;
  cursor: text;
  &:focus {
    border: 1px solid#0c797a;
  }
  @include onTablet {
    padding-left: 30px;
    width: 340px;
  }
  @include onDesktop {
    padding-left: 37px;
    width: 582px;
  }
}

